var SlinkyOption={title:true,speed:200};var ListID="#list_"
var ListCount=0;var ListCountMax=17;function loadList(){ListCount=ListCount+1;ListID="#list_"+ListCount;$(ListID).slinky(SlinkyOption);if(ListCount==ListCountMax){clearInterval(I_LoadList);setTarget();displayList();}}
function setTarget(){var aList=document.getElementsByTagName("a");for(var i=0;i<aList.length;i++){if(aList[i].href.endsWith("#")==false){aList[i].target="_blank";}}}
function displayList(){document.getElementsByTagName("main")[0].style.opacity="1";document.getElementsByClassName("fullscreen-text")[0].style.opacity="0";setTimeout("document.getElementsByClassName('fullscreen-text')[0].style.display = 'none';",1000);}
var I_LoadList=setInterval("loadList()",100);var SearchMode=1;var SearchPanel=document.getElementsByClassName("search-panel")[0];var SearchInput=document.getElementsByClassName("search-input")[0];var SearchList=document.getElementsByTagName("main")[0].getElementsByTagName("a");var SearchText=document.getElementsByClassName("search-input")[0];var SearchResult=document.getElementsByClassName("search-result")[0];var SearchResultItem="";function search(){var SearchListCount=SearchList.length;var SearchWord=SearchText.value.toLowerCase();if(SearchMode==1&&SearchWord!=""){SearchResult.innerHTML="";for(var i=0;i<SearchListCount;i++){if(SearchList[i].innerText.toLowerCase().indexOf(SearchWord)!=-1&&SearchList[i].href.endsWith("#")==false){SearchResultItem=SearchList[i].cloneNode(true);SearchResult.appendChild(SearchResultItem);}}}else if(SearchMode==1&&SearchWord==""){SearchResult.innerHTML="请输入关键词";}else if(SearchMode==2){SearchInput.value="";SearchResult.innerHTML="";SearchPanel.style.visibility="hidden";}}